		// Function from file: hud.dm
		public bool show_hud( bool? version = null ) {
			version = version ?? false;

			int? display_hud_version = null;

			
			if ( !( this.mymob is Mob ) ) {
				return false;
			}

			if ( !( this.mymob.client != null ) ) {
				return false;
			}
			display_hud_version = Lang13.IntNullable( version );

			if ( !Lang13.Bool( display_hud_version ) ) {
				display_hud_version = ( this.hud_version ??0) + 1;
			}

			if ( ( display_hud_version ??0) > 3 ) {
				display_hud_version = 1;
			}

			switch ((int?)( display_hud_version )) {
				case 1:
					this.hud_shown = true;

					if ( this.adding != null ) {
						this.mymob.client.screen.Add( this.adding );
					}

					if ( this.other != null && this.inventory_shown ) {
						this.mymob.client.screen.Add( this.other );
					}

					if ( this.hotkeybuttons != null && !this.hotkey_ui_hidden ) {
						this.mymob.client.screen.Add( this.hotkeybuttons );
					}
					this.action_intent.screen_loc = "EAST-3:24,SOUTH:5";
					this.mymob.client.screen.Add( this.mymob.zone_sel );
					this.mymob.client.screen.Add( this.mymob.healths );
					this.mymob.client.screen.Add( this.mymob.healthdoll );
					this.mymob.client.screen.Add( this.mymob.internals );
					this.mymob.client.screen.Add( this.lingstingdisplay );
					this.mymob.client.screen.Add( this.lingchemdisplay );
					this.hidden_inventory_update();
					this.persistant_inventory_update();
					this.mymob.update_action_buttons();
					this.reorganize_alerts();
					break;
				case 2:
					this.hud_shown = false;

					if ( this.adding != null ) {
						this.mymob.client.screen.Remove( this.adding );
					}

					if ( this.other != null ) {
						this.mymob.client.screen.Remove( this.other );
					}

					if ( this.hotkeybuttons != null ) {
						this.mymob.client.screen.Remove( this.hotkeybuttons );
					}
					this.mymob.client.screen.Remove( this.mymob.zone_sel );
					this.mymob.client.screen.Remove( this.lingstingdisplay );
					this.mymob.client.screen.Remove( this.lingchemdisplay );
					this.mymob.client.screen.Add( this.l_hand_hud_object );
					this.mymob.client.screen.Add( this.r_hand_hud_object );
					this.mymob.client.screen.Add( this.action_intent );
					this.action_intent.screen_loc = "EAST-1:28,SOUTH:5";
					this.hidden_inventory_update();
					this.persistant_inventory_update();
					this.mymob.update_action_buttons();
					this.reorganize_alerts();
					break;
				case 3:
					this.hud_shown = false;

					if ( this.adding != null ) {
						this.mymob.client.screen.Remove( this.adding );
					}

					if ( this.other != null ) {
						this.mymob.client.screen.Remove( this.other );
					}

					if ( this.hotkeybuttons != null ) {
						this.mymob.client.screen.Remove( this.hotkeybuttons );
					}
					this.mymob.client.screen.Remove( this.mymob.zone_sel );
					this.mymob.client.screen.Remove( this.mymob.healths );
					this.mymob.client.screen.Remove( this.mymob.healthdoll );
					this.mymob.client.screen.Remove( this.mymob.internals );
					this.mymob.client.screen.Remove( this.lingstingdisplay );
					this.mymob.client.screen.Remove( this.lingchemdisplay );
					this.hidden_inventory_update();
					this.persistant_inventory_update();
					this.mymob.update_action_buttons();
					this.reorganize_alerts();
					break;
			}
			this.hud_version = display_hud_version;
