				this.ai_hud();
			} else if ( this.mymob is Mob_Living_Silicon_Robot ) {
				this.robot_hud();
			} else if ( this.mymob is Mob_Dead_Observer ) {
				this.ghost_hud();
			} else if ( this.mymob is Mob_Camera_Blob ) {
				this.blob_hud();
			} else if ( this.mymob is Mob_Living_SimpleAnimal_Drone ) {
				this.drone_hud( ui_style );
			} else if ( this.mymob is Mob_Living_SimpleAnimal_Hostile_Swarmer ) {
				this.swarmer_hud();
			} else if ( GlobalFuncs.is_handofgod_god( this.mymob ) ) {
				this.hoggod_hud();
			} else if ( this.mymob is Mob_Living_SimpleAnimal_Hostile_Guardian ) {
				this.guardian_hud();
